const iframeId = 'youtube-iframe';
const YT_ENDED_STATE = 0;  // YouTube's state constant for ended video

export class YoutubePlayerUi extends HTMLElement {
  public youtubePlayer: any;
  private onEndCallback?: () => void;

  // ...existing code...

  onEnd(callback: () => void) {
    this.onEndCallback = callback;
  }

  // ...existing code...
}

async function createYoutubePlayer(iframeId: string) {
  const ready = await isYoutubeIframApiReady();
  if (!ready) {
    console.log('error: isYoutubeIframApiReady is false.');
  }
  return new Promise((resolve) => {
    // @ts-ignore: Defined in https://www.youtube.com/iframe_api
    const player = new window.YT.Player(iframeId, {
        events: {
          'onReady': () => {
            resolve(player);
          },
          'onStateChange': (evt: any) => {
            if (evt.data === YT_ENDED_STATE && (player.element as YoutubePlayerUi).onEndCallback) {
              (player.element as YoutubePlayerUi).onEndCallback();
            }
          },
        }
    });
  });
}

// ...existing code...
```

Now you can register a callback to be notified when the video ends like this:
```typescript
const player = document.querySelector('youtube-player-ui');
player.onEnd(() => {
  console.log('Video finished playing');
});
```

