export class YoutubePlayerUi extends HTMLElement {
  public youtubePlayer: any;
  public onEndCallback?: () => void;
  private urlIdx = 0;

  // ...existing code...

  async initYoutubePlayer(videoId: string, autoplay = false, startAtIdx = 0) {
    this.urlIdx = startAtIdx;
    // ...existing code...
  }

  async playNextVideo() {
    this.urlIdx++;
    const nextVideoId = this.getVideoIdFromUrl(this.getCurrentUrl());
    if (nextVideoId) {
      await this.initYoutubePlayer(nextVideoId, true, this.urlIdx);
    }
  }

  private getCurrentUrl(): string | undefined {
    const urls = (this.parentElement as any)?.gradebookMgr?.project?.matchData?.urls;
    return urls?.[this.urlIdx];
  }

  private getVideoIdFromUrl(url?: string): string | undefined {
    if (!url) return undefined;
    const match = url.match(/(?:youtu\.be\/|youtube\.com(?:\/embed\/|\/v\/|\/watch\?v=|\/user\/\S+|\/ytscreeningroom\?v=|\/sandalsResorts#\w\/\w\/.*\/))([^\/&]{10,12})/);
    return match?.[1];
  }
}

async function createYoutubePlayer(iframeId: string, element: YoutubePlayerUi) {
  // ...existing code...
    const player = new window.YT.Player(iframeId, {
        events: {
          'onReady': () => {
            resolve(player);
          },
          'onStateChange': (evt: any) => {
            if (evt.data === YT.PlayerState.ENDED) {
              if (element.onEndCallback) {
                element.onEndCallback();
              }
              element.playNextVideo();
            }
          },
        }
    });
  // ...existing code...
}
